        sortable: true,
        right: true,
    },
    {
        name: 'Comments',
        selector: 'comments',
        sortable: true,
        right: true
    },
    {
        cell: () => <Button raised primary onClick={handleAction}>Delete</Button>,
        ignoreRowClick: true,
