import React from 'react';
import './App.css';
import { Customers } from './PatientsTable'
import { Header } from './Header'

class App extends React.Component {

  customers = () => {
    let custs = []
    for (let i = 0; i <= 25; i++) {
      custs.push({firstName: `first${i}`, lastName: `last${i}`,
      email: `abc${i}@gmail.com`, address: `000${i} street city, ST`, zipcode: `0000${i}`});
    }
    return custs;
  }

  render() {
    return (
      <div className="App">
        <Header />
        <PatientsTable customers={this.customers()}/>
      </div>
    );
  }
}

export default App;
