import React from 'react';
import './App.css';
import { PatientsTable } from './PatientsTable.js'
import { Header } from './Header.js'
import {ExportCSV} from './ExportCSV.js'

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      patientName: "",
      symptoms: ""
    };

    this.students = [];
    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleInputChange(event) {
    const target = event.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });

  }
  handleSubmit(event) {
    //I think this is causing a problem not making our table dynamic
    event.preventDefault();
  }

  addPatient(event){
    this.students.push({
      name: this.state.patientName, symptoms: this.state.symptoms
    });

  }


  /*
  customers = () => {
    let custs = []
    for (let i = 0; i <= 3; i++) {
      custs.push({
        firstName: `first${i}`, lastName: `last${i}`,
        email: `abc${i}@gmail.com`, address: `000${i} street city, ST`, zipcode: `0000${i}`
      });
    }
    return custs;
  }*/

  render() {
    return (
      <div className="App">
        <form>
          <br />
          <div id='form'>
            <label>
              Patient Name:
          <input
                name="patientName"
                type="string"
                value={this.state.patientName}
                onChange={this.handleInputChange} />
            </label>
            <label>
              Symptoms:
          <input
                name="symptoms"
                type="string"
                value={this.state.symptoms}
                onChange={this.handleInputChange} />
            </label>
            <input type="submit" value="Submit" onClick = {this.addPatient} />
          </div>
        </form>
        <Header />
        <div className="row">
            <div className="col-md-8">
                <h2>Customers</h2>
            </div>
            <div className="col-md-4 center">
                <ExportCSV csvData={this.students} fileName={this.state.fileName} />
            </div>
        </div>
        <PatientsTable customers={this.students}/>
      </div>
    );
  }
}

export default App;
