import React from 'react';
import './App.css';
import { PatientsTable } from './PatientsTable.js'
import { Header } from './Header.js'
import { ExportCSV } from './ExportCSV.js'
import 'bootstrap/dist/css/bootstrap.min.css'

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      patientName: "",
      age: "",
      symptoms: "",
      comments: "",
      roomNumber: "",
    };

    this.patients = [];
    this.handleInputChange = this.handleInputChange.bind(this);
    this.addPatient = this.addPatient.bind(this);
  }

  handleInputChange(event) {
    const target = event.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });

  }
  addPatient(event) {

    event.preventDefault();
    this.patients.push({
      name: this.state.patientName, age: this.state.age,
      symptoms: this.state.symptoms, comments: this.state.comments,
      roomNumber: this.state.roomNumber
    });

    //PREVIOUSLY WHEN WE DIDN't modify state
    //our table would not be updated
    //We have to have state modified for updates to take place in page
    this.setState({
      patientName: "",
      age: "",
      symptoms: "",
      comments: "",
      roomNumber: "",
    });
  }
  //https://reactjs.org/docs/forms.html
  //Use that when we have a working back end
  render() {
    return (
      <div className="App">

        {/* Beginning of Form */}
        <div className="row">
          <div className="col-md-12">
            <Header />
            <ExportCSV csvData={this.patients} fileName={this.state.fileName} />
          </div>
          <div className="col-md-4">
            <form>
              <div class="form-group">
                <label for="patientName">Patient Name: </label>
                <input
                  name="patientName"
                  class="form-control"
                  id="patientName"
                  type="string"
                  placeholder="Enter patient name"
                  value={this.state.patientName}
                  onChange={this.handleInputChange} />
              </div>
              <div class="form-group">
                <label for="patientAge">Patient Age: </label>
                <input
                  name="age"
                  class="form-control"
                  id="patientName"
                  type="number"
                  value={this.state.age}
                  onChange={this.handleInputChange} />
              </div>
              <div class="form-group">
                <label for="patientSymptoms">Patient Symptoms: </label>
                <input
                  name="symptoms"
                  class="form-control"
                  id="patientSymptoms"
                  type="string"
                  value={this.state.symptoms}
                  onChange={this.handleInputChange} />
              </div>
              <div class="form-group">
                <label for="patientComments">Commentss: </label>
                <input
                  name="comments"
                  class="form-control"
                  id="patientComments"
                  type="string"
                  value={this.state.comments}
                  onChange={this.handleInputChange} />
              </div>

              <label>
                Room Number:
          <input
                  name="roomNumber"
                  type="string"
                  value={this.state.roomNumber}
                  onChange={this.handleInputChange} />
              </label>
              <button type="submit" value="Submit" onClick={this.addPatient} >
                Add Patient
              </button>
            </form>
          </div>
          <div className="col-md-8">
            {/* Beginning of Table*/}
            <PatientsTable patients={this.patients} />
          </div>
        </div>
      </div>
    );
  }
}

export default App;
