import React from 'react';
import './App.css';
import { PatientsTable } from './PatientsTable.js'
import { Header } from './Header.js'
import {ExportCSV} from './ExportCSV.js'

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      patientName: "",
      symptoms: ""
    };

    this.patients = [];
    this.handleInputChange = this.handleInputChange.bind(this);
    //this.handleSubmit = this.handleSubmit.bind(this);
    this.addPatient = this.addPatient.bind(this);
  }

  handleInputChange(event) {
    const target = event.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });

  }
  addPatient(event){
    this.patients.push({
      name: this.state.patientName, symptoms: this.state.symptoms
    });

    this.setState({
      name: "",
      symptoms
    });
  }
  //https://reactjs.org/docs/forms.html
  //Use that when we have a working back end
  render() {
    return (
      <div className="App">
          <br />
          <div id='form'>
            <label>
              Patient Name:
          <input
                name="patientName"
                type="string"
                value={this.state.patientName}
                onChange={this.handleInputChange} />
            </label>
            <label>
              Symptoms:
          <input
                name="symptoms"
                type="string"
                value={this.state.symptoms}
                onChange={this.handleInputChange} />
            </label>
            <button type="submit" value="Submit" onClick = {this.addPatient} >
              Add Patient
              </button>
          </div>
        <Header />
        <div className="row">
            <div className="col-md-8">
            </div>
            <div className="col-md-4 center">
                <ExportCSV csvData={this.patients} fileName={this.state.fileName} />
            </div>
        </div>
        <PatientsTable patients={this.patients}/>
      </div>
    );
  }
}

export default App;
