import React from 'react';
import './App.css';
import './Table.css';

class NameForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      patientName: "",
      symptoms: "",
      students: [
        { name: 'Wasif', symptoms: 'wasif@email.com' }
     ]
    };

    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleInputChange(event) {
    const target = event.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const name = target.name;
    this.setState({
      [name]: value
    });
  }

  renderTableData() {
    return this.state.students.map((student, index) => {
    const { name, symptoms } = student //destructuring
    return (
        <tr>
            <td>{name}</td>
            <td>{symptoms}</td>
        </tr>
    )
    })
  }   

  renderTableHeader() {
      let header = Object.keys(this.state.students[0])
      return header.map((key, index) => {
        return <th key={index}>{key.toUpperCase()}</th>
      })
  }

  handleSubmit(event) {

    event.preventDefault(); //This stops form from default behaviour of refreshing page

    /*
    alert('A name was submitted: ' + this.state.patientName + ', Here are the symptoms '
      + this.state.symptoms);*/
    console.log(this.state.students)
    //this.state.students.add('Jordan','Femur Broke');

    this.setState(prevState => ({
      arrayvar: [...prevState.arrayvar, newelement]
    }))
    console.log(this.state.students)
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <br />
        <div id = 'form'>
          <label>
            Patient Name:
          <input
              name="patientName"
              type="string"
              value={this.state.patientName}
              onChange={this.handleInputChange} />
          </label>
          <br />
          <label>
            Symptoms:
          <input
              name="symptoms"
              type="string"
              value={this.state.symptoms}
              onChange={this.handleInputChange} />
          </label>
        </div>
        <input type="submit" value="Submit" />
        <div>
            <h1 id='title'>Enrolled Patients</h1>
              <table id='students'>
                 <tbody>
                    <tr>{this.renderTableHeader()}</tr>
                    {this.renderTableData()}
                 </tbody>
              </table>
           </div>
      </form>
    );
  }
}

export default NameForm;
