import {Component} from "react";
import React from "react";
import { Header } from './Header.js'
import { ExportCSV } from './ExportCSV.js'

class PatientEntryForm extends Component {

    constructor(props) {
        super(props);
        /*
        this.state = {
          patientName: "",
          age: "",
          symptoms: "",
          comments: "",
          roomNumber: "",
        };*/


        this.handleInputChange = this.handleInputChange.bind(this);
        this.addPatient = this.addPatient.bind(this);
      }

      handleInputChange(event) {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        this.setState({
          [name]: value
        });

      }
      addPatient(event) {

        this.addPatientRequst();
        event.preventDefault(); //So the form does not submit automatically and refresh page
        this.patients.push({
          patientName: this.state.patientName, age: this.state.age,
          symptoms: this.state.symptoms, comments: this.state.comments,
          roomNumber: this.state.roomNumber
        });
        //PREVIOUSLY WHEN WE DIDN't modify state
        //our table would not be updated
        //We have to have state modified for updates to take place in page
        this.setState({
          patientName: "",
          age: "",
          symptoms: "",
          comments: "",
          roomNumber: "",
        });
      }


    render() {
        return (
          <div className="App">
            {/* Beginning of Form */}
            <div className="row">
              <div className="col-md-12">
        <Header/> {/*<ExportCSV csvData={this.patients} fileName={this.state.fileName} />*/}
              </div>
              <div className="col-md-4" class="m-5">
                <form>
                  <div class="form-group">
                    <label for="patientName">Patient Name: </label>
                    <input
                      name="patientName"
                      class="form-control"
                      id="patientName"
                      type="string"
                      placeholder="Enter patient name"
                      value={this.state.patientName}
                      onChange={this.handleInputChange} />
                  </div>
                  <div class="form-group">
                    <label for="patientAge">Patient Age: </label>
                    <input
                      name="age"
                      class="form-control"
                      id="patientName"
                      type="number"
                      placeholder="Enter patient age"
                      value={this.state.age}
                      onChange={this.handleInputChange} />
                  </div>
                  <div class="form-group">
                    <label for="patientSymptoms">Patient Symptoms: </label>
                    <input
                      name="symptoms"
                      class="form-control"
                      id="patientSymptoms"
                      type="string"
                      placeholder="Enter patient symptoms"
                      value={this.state.symptoms}
                      onChange={this.handleInputChange} />
                  </div>
                  <div class="form-group">
                    <label for="patientComments">Comments: </label>
                    <input
                      name="comments"
                      class="form-control"
                      id="patientComments"
                      type="string"
                      placeholder="Comments about patient"
                      value={this.state.comments}
                      onChange={this.handleInputChange} />
                  </div>
                  <div class="form-group">
                    <label for="patientRoomNumber">Room Number: </label>
                    <input
                      name="roomNumber"
                      class="form-control"
                      id="patientRoomNumber"
                      type="number"
                      placeholder="Enter patient room number"
                      value={this.state.roomNumber}
                      onChange={this.handleInputChange} />
                  </div>
                  <button class="btn btn-primary" onClick={this.addPatient} >
                    Add Patient
                  </button>
                </form>
              </div>
              <div className="col-md-8" class="m-5">
              </div>
            </div>
          </div>
        );
      }
  }

  export default PatientEntryForm;
