import React, { Component } from 'react'
import './Table.css';

//https://dev.to/abdulbasit313/an-easy-way-to-create-a-customize-dynamic-table-in-react-js-3igg]
//Comes from the above website


class Table extends Component {
   constructor(props) {
      super(props) //since we are extending class Table so we have to use super in order to override Component class constructor
      this.state = { //state is by default an object
         students: [
            { name: 'Wasif', symptoms: 'wasif@email.com' },
            { name: 'Ali', symptoms: 'ali@email.com' },
            { name: 'Saad', symptoms: 'saad@email.com' },
            { name: 'Asad', symptoms: 'asad@email.com' }
         ]
      }
   }

    renderTableData() {
        return this.state.students.map((student, index) => {
        const { name, symptoms } = student //destructuring
        return (
            <tr>
                <td>{name}</td>
                <td>{symptoms}</td>
            </tr>
        )
        })
    }

    renderTableHeader() {
        let header = Object.keys(this.state.students[0])
        return header.map((key, index) => {
           return <th key={index}>{key.toUpperCase()}</th>
        })
     }

     render() {
        return (
           <div>
              <h1 id='title'>Enrolled Patients</h1>
              <table id='students'>
                 <tbody>
                    <tr>{this.renderTableHeader()}</tr>
                    {this.renderTableData()}
                 </tbody>
              </table>
           </div>
        )
     }
}

export default Table //exporting a component make it reusable and this is the beauty of react
