import React from 'react';
import './App.css';
import './Table.css';
import Table from './Table';

class NameForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      patientName: "",
      symptoms: "",
    };

    //this.students = [{ name: 'Wasif', symptoms: 'Elbow Hurts' }];
    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleInputChange(event) {
    const target = event.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });

  }

  renderTableData() {
    return this.students.map((student, index) => {
      const { name, symptoms } = student //destructuring
      return (
        <tr key={index}>
          <td>{name}</td>
          <td>{symptoms}</td>
        </tr>
      )
    })
  }

  renderTableHeader() {
    let header = Object.keys(this.students[0])
    return header.map((key, index) => {
      return <th key={index}>{key.toUpperCase()}</th>
    })
  }

  handleSubmit(event) {

    //This stops form from default behaviour of refreshing page
    this.students.push({ name: this.state.patientName.toString(), symptoms: this.state.symptoms.toString() });
    this.renderTableData();

    //I think this is causing a problem not making our table dynamic
    event.preventDefault();
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <br />
        <br />
        <br />
        <div id='form'>
          <label>
            Patient Name:
          <input
              name="patientName"
              type="string"
              value={this.state.patientName}
              onChange={this.handleInputChange} />
          </label>
          <label>
            Symptoms:
          <input
              name="symptoms"
              type="string"
              value={this.state.symptoms}
              onChange={this.handleInputChange} />
          </label>
          <input type="submit" value="Submit" />
        </div>
        <div>
          <h1 id='title'>Enrolled Patients</h1>
          <table id='students'>
            <tbody>
              <tr>{this.renderTableHeader()}</tr>
              {this.renderTableData()}
            </tbody>
          </table>
        </div>
      </form>
    );
  }
}

export default NameForm;
