import React from 'react';
import './App.css';
import { PatientsTable } from './PatientsTable.js'
import { Header } from './Header.js'
import { ExportCSV } from './ExportCSV.js'
import 'bootstrap/dist/css/bootstrap.min.css'

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      patientName: "",
      age: "",
      symptoms: "",
      comments: "",
      roomNumber: "",
    };

    this.patients = [];
    this.handleInputChange = this.handleInputChange.bind(this);
    this.addPatient = this.addPatient.bind(this);
  }

  handleInputChange(event) {
    const target = event.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });

  }
  addPatient(event) {
    this.patients.push({
      name: this.state.patientName, age: this.state.age,
      symptoms: this.state.symptoms, comments: this.state.comments,
      roomNumber: this.state.roomNumber
    });

    //PREVIOUSLY WHEN WE DIDN't modify state
    //our table would not be updated
    //We have to have state modified for updates to take place in page
    this.setState({
      patientName: "",
      age: "",
      symptoms: "",
      comments: "",
      roomNumber: "",
    });
  }
  //https://reactjs.org/docs/forms.html
  //Use that when we have a working back end
  render() {
    return (
      <div className="App">

        <Header ></Header>
        <ExportCSV csvData={this.patients} fileName={this.state.fileName} />
        {/* Beginning of Form */}
        <div id='form' className="row">
          <div className="col-md-4">
            <label>
              Patient Name:
          <input
                name="patientName"
                type="string"
                value={this.state.patientName}
                onChange={this.handleInputChange} />
            </label>
            <label>
              Age:
          <input
                name="age"
                type="string"
                value={this.state.age}
                onChange={this.handleInputChange} />
            </label>
            <label>
              Symptoms:
          <input
                name="symptoms"
                type="string"
                value={this.state.symptoms}
                onChange={this.handleInputChange} />
            </label>
            <label>
              Comments:
          <input
                name="comments"
                type="string"
                value={this.state.comments}
                onChange={this.handleInputChange} />
            </label>
            <label>
              Room Number:
          <input
                name="roomNumber"
                type="string"
                value={this.state.roomNumber}
                onChange={this.handleInputChange} />
            </label>
            <button type="submit" value="Submit" onClick={this.addPatient} >
              Add Patient
              </button>
          </div>
        </div>

        {/* Beginning of Table*/}
        <div className="row">
          <div className="col-md-8">
          <PatientsTable patients={this.patients} />
          </div>
          <div className="col-md-4">
          </div>
        </div>
      </div>
    );
  }
}

export default App;
